new Vue({el:"#app",data:{news:[],inputHeader:"",inputText:"",inputUrl:"",articles:[],articlesCNN:[],url:"https://www.google.com",headBBC:"BBC News",headCNN:"CNN News"},mounted:function(){setInterval(this.refresh,1e3);this.refresh();console.log("mounted workes");this.$http.get("/api/sticker").then(function(n){this.news=n.body.reverse();console.log(n)});console.log("this is a second vue app");this.$http.get("https://newsapi.org/v1/articles?source=bbc-news&sortBy=top&apiKey=6508abdd35e74474aa46920aee671081").then(function(n){this.articles=n.body.articles;console.log(n);console.log("inside bbc-api")});console.log("this is a third vue app");this.$http.get("https://newsapi.org/v1/articles?source=cnn&sortBy=top&apiKey=6508abdd35e74474aa46920aee671081").then(function(n){this.articlesCNN=n.body.articles;console.log(n);console.log("inside CNN-api")})},methods:{addNews:function(){if(this.inputHeader.length===0||this.inputText.length===0)return!1;var n={header:this.inputHeader,text:this.inputText,url:this.inputUrl};this.$http.post("/api/sticker",n).then(function(n){this.news.push(n.body);this.news="";this.inputHeader="";this.inputText="";this.inputUrl="";this.$http.get("/api/sticker").then(function(n){this.news=n.body.reverse();console.log(n)})})},updateNews:function(n){console.log("Update news");var t={header:this.inputHeader,id:n.id,text:this.inputText,url:this.inputUrl};console.log(n.id);this.$http.put("/api/sticker/"+n.id,t).then(function(){})},deleteNews:function(n){console.log("Delete news "+n)},refresh:function(){this.$http.get("/api/sticker").then(function(n){this.news=n.body.reverse()})}}});